contract SystemContract {
    modifier systemContract(address addr) {
        bytes memory addrBytes = abi.encodePacked(addr);
        for (uint i = 0; i < 12; i++) {
            require(addrBytes[i] == 0, "not a system contract");
        }
        _;
    }
}

