        uint256 positionId,
        uint256 entryIndex
    ) external view returns (address, uint256) {
        address addr = _idToRaffleData[positionId].indexToAddress[entryIndex];
        return (addr, _idToRaffleData[positionId].addressToAmount[addr]);
    }

