// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract TorumoniERC1155 is ERC1155, Ownable {

    uint256 public constant maxSupply = 360;
    uint256 public currentSupply;
    uint256 public cost;
    uint256 public costAmount;
    bool public paused;

    uint256 private supply;
    mapping(uint256 => mapping(address => uint256)) private _capacities;

    constructor(
        string memory uri_,
        uint256 _cost,
        uint256 _costAmount
    ) ERC1155(uri_) {
        supply = 1;
        paused = false;
        currentSupply = 100;
        cost = _cost;
        costAmount = _costAmount;
        mint(msg.sender, 10001);
    }

    function mint(address _to, uint256 _mintAmount) public payable {
        require(!paused);
        require(supply <= currentSupply);

        uint256 totalCost = cost;
        if (_mintAmount > 2) {
            totalCost += (_mintAmount - 2) * costAmount;
        }
        require(msg.value >= totalCost || msg.sender == owner());
        _mint(_to, supply, _mintAmount, "");
        supply += 1;
    }

    function setSupply(uint256 _currentSupply) external onlyOwner {
        currentSupply = _currentSupply;
    }

    function setCost(uint256 _newcost) public onlyOwner {
        cost = _newcost;
    }

    function pause(bool _state) public onlyOwner {
        paused = _state;
    }

    function withdraw() public payable onlyOwner {
        (bool success, ) = payable(msg.sender).call{
            value: address(this).balance
        }("");
        require(
            success,
            "Address: unable to send value, sender may have reverted"
        );
    }
}

