//SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

contract ReefCardTopUpSender {
    address public owner;
    uint256 public minTopUpValue;
    uint256 public maxTopUpValue;
    mapping(address => bool) public isWhitelisted;
    address[] private _whitelist;

    event OwnerChanged(address indexed oldOwner, address indexed newOwner);
    event MinTopUpValueChanged(uint256 indexed oldValue, uint256 indexed newValue);
    event MaxTopUpValueChanged(uint256 indexed oldValue, uint256 indexed newValue);
    event WhitelistChanged(address[] addressList, bool added);
    event TopUp(address indexed fromAddr, address indexed cardAddress, uint256 amount);
    event Withdrawn(uint256 amount);

    modifier onlyOwner() {
        require(owner == msg.sender, "Caller is not owner");
        _;
    }

    constructor(
        address _owner,
        uint256 _minTopUpValue,
        uint256 _maxTopUpValue
    ) {
        owner = _owner;
        minTopUpValue = _minTopUpValue;
        maxTopUpValue = _maxTopUpValue;
    }

    function transferOwnership(address newOwner) external onlyOwner {
        address oldOwner = owner;
        owner = newOwner;
        emit OwnerChanged(oldOwner, newOwner);
    }

    function setMinTopUpValue(uint256 newMinTopUpValue) external onlyOwner {
        require(newMinTopUpValue <= maxTopUpValue, "MinTopUpValue higher than MaxTopUpValue");

        uint256 oldMinTopUpValue = minTopUpValue;
        minTopUpValue = newMinTopUpValue;
        emit MinTopUpValueChanged(oldMinTopUpValue, newMinTopUpValue);
    }

    function setMaxTopUpValue(uint256 newMaxTopUpValue) external onlyOwner {
        require(newMaxTopUpValue >= minTopUpValue, "MaxTopUpValue lower than MinTopUpValue");

        uint256 oldMaxTopUpValue = maxTopUpValue;
        maxTopUpValue = newMaxTopUpValue;
        emit MaxTopUpValueChanged(oldMaxTopUpValue, newMaxTopUpValue);
    }

    function addToWhitelist(address[] calldata addressList) external onlyOwner {
        uint256 totalAddresses = addressList.length;
        for (uint256 i; i < totalAddresses; ++i) {
            address addr = addressList[i];
            if (!isWhitelisted[addr]) {
                isWhitelisted[addr] = true;
                _whitelist.push(addr);
            }
        }

        emit WhitelistChanged(addressList, true);
    }

    function removeFromWhitelist(address[] calldata addressList) external onlyOwner {
        uint256 totalAddresses = addressList.length;
        for (uint256 i; i < totalAddresses; ++i) {
            address addr = addressList[i];
            uint256 totalWhitelisted = _whitelist.length;
            if (isWhitelisted[addr]) {
                isWhitelisted[addr] = false;
                for (uint256 j; j < totalWhitelisted; ++j) {
                    if (_whitelist[j] == addr) {
                        _whitelist[j] = _whitelist[totalWhitelisted - 1];
                        _whitelist.pop();
                        break;
                    }
                }
            }
        }

        emit WhitelistChanged(addressList, false);
    }

    function getWhitelist() external view onlyOwner returns (address[] memory) {
        return _whitelist;
    }

    function withdraw() external onlyOwner {
        uint256 amount = getBalance();
        payable(msg.sender).transfer(amount);

        emit Withdrawn(amount);
    }

    function getBalance() public view returns (uint256) {
        return address(this).balance;
    }

    function topUp(address cardAddress) external payable {
        require(msg.value >= minTopUpValue, "Value sent too low");
        require(msg.value <= maxTopUpValue, "Value sent too high");
        require(isWhitelisted[cardAddress], "Not valid address");

        emit TopUp(msg.sender, cardAddress, msg.value);
    }
}

