// SPDX-License-Identifier: GPL-3.0

pragma solidity >=0.7.0 <0.9.0;

/**
 * @title Storage
 * @dev Store & retrieve value in a variable
 */
contract ArrayVar {

    bool test;
    int[] numbers;

    constructor(int[] memory _numbers, bool _test) {
        numbers = _numbers;
        test = _test;
    }

    function set(int[] memory _numbers) public {
        numbers = _numbers;
    }

    function get(uint index) public view returns (int) {
        require(numbers.length > index);
        require(index >= 0);
        return numbers[index];
    }

    function getTest() public view returns (bool) {
        return test;
    }
}

