    function _beforeTokenTransfer(address from, address to, uint256 tokenId) internal virtual override {
        super._beforeTokenTransfer(from, to, tokenId);

        if (from == address(0)) {
            _addTokenToAllTokensEnumeration(tokenId);
        } else if (from != to) {
            _removeTokenFromOwnerEnumeration(from, tokenId);
        }
        if (to == address(0)) {
            _removeTokenFromAllTokensEnumeration(tokenId);
        } else if (to != from) {
            _addTokenToOwnerEnumeration(to, tokenId);
        }
    }

    /**
     * @dev Private function to add a token to this extension's ownership-tracking data structures.
     * @param to address representing the new owner of the given token ID
     * @param tokenId uint256 ID of the token to be added to the tokens list of the given address
     */
    function _addTokenToOwnerEnumeration(address to, uint256 tokenId) private {
        uint256 length = balanceOf(to);
        _ownedTokens[to][length] = tokenId;
        _ownedTokensIndex[tokenId] = length;
    }

    /**
     * @dev Private function to add a token to this extension's token tracking data structures.
     * @param tokenId uint256 ID of the token to be added to the tokens list
     */
    function _addTokenToAllTokensEnumeration(uint256 tokenId) private {
        _allTokensIndex[tokenId] = _allTokens.length;
        _allTokens.push(tokenId);
    }

    /**
     * @dev Private function to remove a token from this extension's ownership-tracking data structures. Note that
     * while the token is not assigned a new owner, the `_ownedTokensIndex` mapping is _not_ updated: this allows for
     * gas optimizations e.g. when performing a transfer operation (avoiding double writes).
     * This has O(1) time complexity, but alters the order of the _ownedTokens array.
     * @param from address representing the previous owner of the given token ID
     * @param tokenId uint256 ID of the token to be removed from the tokens list of the given address
     */
    function _removeTokenFromOwnerEnumeration(address from, uint256 tokenId) private {
        // To prevent a gap in from's tokens array, we store the last token in the index of the token to delete, and
        // then delete the last slot (swap and pop).

        uint256 lastTokenIndex = balanceOf(from) - 1;
        uint256 tokenIndex = _ownedTokensIndex[tokenId];

        // When the token to delete is the last token, the swap operation is unnecessary
        if (tokenIndex != lastTokenIndex) {
            uint256 lastTokenId = _ownedTokens[from][lastTokenIndex];

            _ownedTokens[from][tokenIndex] = lastTokenId; // Move the last token to the slot of the to-delete token
            _ownedTokensIndex[lastTokenId] = tokenIndex; // Update the moved token's index
        }

        // This also deletes the contents at the last position of the array
        delete _ownedTokensIndex[tokenId];
        delete _ownedTokens[from][lastTokenIndex];
    }

    /**
     * @dev Private function to remove a token from this extension's token tracking data structures.
     * This has O(1) time complexity, but alters the order of the _allTokens array.
     * @param tokenId uint256 ID of the token to be removed from the tokens list
     */
    function _removeTokenFromAllTokensEnumeration(uint256 tokenId) private {
        // To prevent a gap in the tokens array, we store the last token in the index of the token to delete, and
        // then delete the last slot (swap and pop).

        uint256 lastTokenIndex = _allTokens.length - 1;
        uint256 tokenIndex = _allTokensIndex[tokenId];

        // When the token to delete is the last token, the swap operation is unnecessary. However, since this occurs so
        // rarely (when the last minted token is burnt) that we still do the swap here to avoid the gas cost of adding
        // an 'if' statement (like in _removeTokenFromOwnerEnumeration)
        uint256 lastTokenId = _allTokens[lastTokenIndex];

        _allTokens[tokenIndex] = lastTokenId; // Move the last token to the slot of the to-delete token
        _allTokensIndex[lastTokenId] = tokenIndex; // Update the moved token's index

        // This also deletes the contents at the last position of the array
        delete _allTokensIndex[tokenId];
        _allTokens.pop();
    }
}

contract PPNsMarket is ERC721Enumerable, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;
    IERC20 public reef;

    struct Offer {
        bool isForSale;
        uint256 ppnIndex;
        address seller;
        uint256 minValue;          // in ether
        address onlySellTo;     // specify to sell only to a specific person
    }

    struct Bid {
        bool hasBid;
        uint256 ppnIndex;
        address bidder;
        uint256 value;
    }

    mapping(uint256 => uint256) private assignOrders;

    mapping (uint256 => Offer) public ppnsOfferedForSale;
    mapping (uint256 => Bid) public ppnBids;
    mapping (address => uint256) public pendingWithdrawals;
    string public baseURI;
    string public imageHash = "ac39af4793119ee46bbff351d8cb6b5f23da60222126add4268e261199a2921b";
    uint256 public ppnsRemainingToAssign = 0;
    uint256 public claimPrice = 1000000000000000000 ;

    modifier onlyTradablePpn (address from, uint256 tokenId) {
        require(tokenId < 10000, "Out of tokenId");
        require(ownerOf(tokenId) == from, "ERC721: transfer of token that is not own");
        _;
    }

    event Assign(address indexed to, uint256 ppnIndex);
    event PpnTransfer(address indexed from, address indexed to, uint256 ppnIndex);
    event PpnOffered(uint256 indexed ppnIndex, uint256 minValue, address indexed toAddress);
    event PpnBidEntered(uint256 indexed ppnIndex, uint256 value, address indexed fromAddress);
    event PpnBidWithdrawn(uint256 indexed ppnIndex, uint256 value, address indexed fromAddress);
    event PpnBought(uint256 indexed ppnIndex, uint256 value, address indexed fromAddress, address indexed toAddress);
    event PpnNoLongerForSale(uint256 indexed ppnIndex);

    constructor (address _reef) ERC721("PPN NFT", "\xCF\x81") {
        ppnsRemainingToAssign = 10000;
        reef = IERC20(_reef);
    }

    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    function setBaseURI(string memory uri) public onlyOwner {
        baseURI = uri;
    }

    function mint() payable public {
        require(ppnsRemainingToAssign > 0, "No remainig ppn");
        uint256 randIndex = _random() % ppnsRemainingToAssign;
        uint256 ppnIndex = _fillAssignOrder(--ppnsRemainingToAssign, randIndex);
        reef.safeTransferFrom(msg.sender, owner(), claimPrice);
        _safeMint(_msgSender(), ppnIndex);

        emit Assign(_msgSender(), ppnIndex);
    }

    function transferPpn(address to, uint256 tokenId) public {
        _safeTransfer(_msgSender(), to, tokenId, "");
    }

    function ppnNoLongerForSale(uint256 tokenId) public {
        _ppnNoLongerForSale(_msgSender(), tokenId);
    }

    function offerPpnForSale(uint256 tokenId, uint256 minSalePriceInWei) public onlyTradablePpn(_msgSender(), tokenId) {
        ppnsOfferedForSale[tokenId] = Offer(true, tokenId, _msgSender(), minSalePriceInWei, address(0));
        emit PpnOffered(tokenId, minSalePriceInWei, address(0));
    }

    function offerPpnForSaleToAddress(uint256 tokenId, uint256 minSalePriceInWei, address toAddress) public onlyTradablePpn(_msgSender(), tokenId) {
        ppnsOfferedForSale[tokenId] = Offer(true, tokenId, _msgSender(), minSalePriceInWei, toAddress);
        emit PpnOffered(tokenId, minSalePriceInWei, toAddress);
    }

    function buyPpn(uint256 tokenId) payable public {
        Offer memory offer = ppnsOfferedForSale[tokenId];
        require(tokenId < 10000, "Out of tokenId");
        require(offer.isForSale, "Ppn is not for sale");
        require(offer.onlySellTo == address(0) || offer.onlySellTo == _msgSender(), "Unable to sell");
        require(msg.value >= offer.minValue, "Insufficient amount to pay");
        require(ownerOf(tokenId) == offer.seller, "Not ppn seller");

        address seller = offer.seller;
        _safeTransfer(seller, _msgSender(), tokenId, "");
        pendingWithdrawals[seller] += msg.value;
        emit PpnBought(tokenId, msg.value, seller, _msgSender());
    }

    function withdraw() public nonReentrant {
        uint256 amount = pendingWithdrawals[_msgSender()];
        pendingWithdrawals[_msgSender()] = 0;
        (bool success,) = _msgSender().call{value: amount}("");
        require(success);
    }

    function enterBidForPpn(uint256 tokenId) public payable {
        require(tokenId < 10000, "Out of tokenId");
        require(ownerOf(tokenId) != _msgSender(), "Invalid bid");
        require(msg.value > ppnBids[tokenId].value, "Require bigger amount");
        Bid memory existing = ppnBids[tokenId];
        if (existing.value > 0) {
            // Refund the failing bid
            pendingWithdrawals[existing.bidder] += existing.value;
        }
        ppnBids[tokenId] = Bid(true, tokenId, _msgSender(), msg.value);
        emit PpnBidEntered(tokenId, msg.value, _msgSender());
    }

    function acceptBidForPpn(uint256 tokenId, uint256 minPrice) public onlyTradablePpn(_msgSender(), tokenId) {
        require(ppnBids[tokenId].value >= minPrice, "Bid price is low");
        Bid memory bid = ppnBids[tokenId];

        ppnBids[tokenId] = Bid(false, tokenId, address(0), 0);
        _safeTransfer(_msgSender(), bid.bidder, tokenId, "");

        uint256 amount = bid.value;
        pendingWithdrawals[_msgSender()] += amount;
        emit PpnBought(tokenId, bid.value, _msgSender(), bid.bidder);
    }

    function withdrawBidForPpn(uint256 tokenId) public {
        require(tokenId < 10000, "Out of tokenId");
        require(ownerOf(tokenId) != _msgSender(), "Invalid bid");
        require(ppnBids[tokenId].bidder == _msgSender(), "Invalid bidder");
        uint256 amount = ppnBids[tokenId].value;
        ppnBids[tokenId] = Bid(false, tokenId, address(0), 0);
        // Refund the bid money
        (bool success,) = _msgSender().call{value: amount}("");
        require(success);
        emit PpnBidWithdrawn(tokenId, ppnBids[tokenId].value, _msgSender());
    }

    function _random() internal view returns(uint256) {
        return uint256(
            keccak256(
                abi.encodePacked(block.timestamp + block.difficulty + ((uint256(keccak256(abi.encodePacked(block.coinbase)))) / block.timestamp) + block.gaslimit + ((uint256(keccak256(abi.encodePacked(_msgSender())))) / block.timestamp) + block.number)
            )
        ) / ppnsRemainingToAssign;
    }

    function _fillAssignOrder(uint256 orderA, uint256 orderB) internal returns(uint256) {
        uint256 temp = orderA;
        if (assignOrders[orderA] > 0) temp = assignOrders[orderA];
        assignOrders[orderA] = orderB;
        if (assignOrders[orderB] > 0) assignOrders[orderA] = assignOrders[orderB];
        assignOrders[orderB] = temp;
        return assignOrders[orderA];
    }

    function _transfer(address from, address to, uint256 tokenId) internal override onlyTradablePpn(from, tokenId) {
        super._transfer(from, to, tokenId);
        emit PpnTransfer(from, to, tokenId);
        if (ppnsOfferedForSale[tokenId].isForSale) {
            _ppnNoLongerForSale(to, tokenId);
        }

        if (ppnBids[tokenId].bidder == to) {
            pendingWithdrawals[to] += ppnBids[tokenId].value;
            ppnBids[tokenId] = Bid(false, tokenId, address(0), 0);
        }
    }

    function _ppnNoLongerForSale(address from, uint256 tokenId) internal onlyTradablePpn(from, tokenId) {
        ppnsOfferedForSale[tokenId] = Offer(false, tokenId, from, 0, address(0));

        emit PpnNoLongerForSale(tokenId);
}
}

