contract JungleRaceBridge is Ownable{
    
    using SafeMath for uint256;
    // ECDSA Address
    using ECDSA for address;

    address public token;
    bool public pause;
    uint256 public fees;

    modifier isLocked(){
        require(!pause,"contract locked");
        _;
    } 
    
    address private signer;
    address private deployer;
    uint256 public expiryBlock = 10 minutes; 
    
    // Signature Message Hash
    mapping(bytes32 => bool)public msgHash;
    mapping(bytes32 => bool) public txHash;
    
    event TransferEthToken(address sender, address receiver, uint256 amount, bytes32 txHash );
    event Deposit(address sender, address receiver, uint256 amount);
    event SetExpiryBlock(address sender, uint256 expiryBlock);
    event SetStatus(address sender, bool status);

    constructor (address _token, address _signer, address _deployer,uint256 _fees ) {
        token = _token;
        signer = _signer;
        deployer = _deployer;
        fees = _fees;
    }  
          
    function deposit(uint256 amount) external isLocked {
        bool status = IERC20(token).transferFrom(msg.sender,address(this),amount);
        require(status == true);
        emit Deposit(msg.sender,address(this),amount);

    }

    function claimToken(uint256 _amount, bytes32 txs, bytes calldata signature, uint256 deadline) external isLocked {
        
        require( (deadline > block.timestamp) && (deadline < (block.timestamp + expiryBlock)) , "Time Expired" );

        //messageHash can be used only once
        require(!txHash[txs], "BridgeROSEtoBSC: tx duplicate");
        bytes32 messageHash = message(msg.sender, _amount, txs, signer,deployer,deadline);
        require(!msgHash[messageHash], "BridgeROSEtoBSC: signature duplicate");
        
         //Verifes signature    
        address src = verifySignature(messageHash, signature);
        require(signer == src, "BridgeROSEtoBSC: unauthorized");
        
        txHash[txs] = true;
        msgHash[messageHash] = true;

        if(fees>0){
            IERC20(token).transfer(owner(), ((_amount * fees)/100));
            _amount = _amount - ((_amount * fees)/100);
        }
        IERC20(token).transfer(msg.sender, _amount);
    }

    function changeFee(uint256 _fees) external onlyOwner {
        fees = _fees;
    }
    
    /**
    * @dev Ethereum Signed Message, created from `hash`
    * @dev Returns the address that signed a hashed message (`hash`) with `signature`.
    */
    function verifySignature(bytes32 _messageHash, bytes memory _signature) 
        public pure returns (address signatureAddress)
    {
        bytes32 hash = ECDSA.toEthSignedMessageHash(_messageHash);
        signatureAddress = ECDSA.recover(hash, _signature);
    }
    
    /**
    * @dev Returns hash for given data
    */
    function message(address  _sender , uint256 _amount, bytes32 _txHash, address _verifier, address _deployer,uint256 deadline) public pure returns(bytes32)
    {
        return keccak256(abi.encodePacked(_sender, _amount ,_txHash, _verifier, _deployer,deadline));
    }

    function getChainId() public view returns (uint) {
        uint256 chainId;
        assembly { chainId := chainid() }
        return chainId;
    }
    
    /**
     * @notice withdraw Owner can withdraw pending tokens from contract.
     * @param tokenAddr  token address. 
     */
    function withdraw(address tokenAddr, address receiver) external onlyOwner{
        // Owner call check
        // Pending token transfer
        IERC20(tokenAddr).transfer(receiver, IERC20(tokenAddr).balanceOf(address(this)));

    }

    function setStatus(bool _status ) external onlyOwner {
        pause=_status;
        emit SetStatus(msg.sender,  _status);
    }
        
   function setexpiryBlock( uint256 _expiryBlock ) external onlyOwner {
       expiryBlock = _expiryBlock;
       emit SetExpiryBlock(msg.sender,  _expiryBlock);
   }

   function setSigner( address _signer ) external onlyOwner {
       signer = _signer;
   }
}

