//SPDX-License-Identifier:MIT

pragma solidity >=0.8.4;

contract Crud{
    uint256 coins = 0;

    function add()public{
        coins+=1;
    }
    function sub()public{
        coins-=1;
    }
    function setValue(uint256 _coins)public{
        coins = _coins;
    }
}

