// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721BurnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721URIStorageUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";

import "./IERC2981.sol";

contract ArtistNFTUpgradeable is
    Initializable,
    ERC721Upgradeable,
    ERC721EnumerableUpgradeable,
    ERC721BurnableUpgradeable,
    ERC721URIStorageUpgradeable,
    AccessControlUpgradeable,
    PausableUpgradeable,
    IERC2981
{
    using CountersUpgradeable for CountersUpgradeable.Counter;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    uint256 private constant INVERSE_BASIS_POINTS = 1000;
    CountersUpgradeable.Counter private _tokenIdCounter;
    string public baseURI;
    // royalty percentage cut of artist
    uint256 artistRoyalty;

    // mapping of token id to original creator
    mapping(uint256 => address) public tokenIdToCreator;

    event ArtistNFTMinted(
        uint256 tokenID,
        uint256 amountMinted,
        address contractAddress,
        string tokenMetadataUri,
        address creator
    );

    /**
     * @dev Initializer
     */
    function initialize() public initializer {
        __ERC721_init("ArtistNFT", "AFT");
        __ERC721Enumerable_init();
        __ERC721Burnable_init();
        __ERC721URIStorage_init();
        __AccessControl_init();
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(MINTER_ROLE, msg.sender);
        // set royalties cut %
        artistRoyalty = 50; // 5%
        baseURI = "https://isekaiverse.mypinata.cloud/ipfs/";
    }

    function safeMint(
        address to,
        address _creator,
        uint256 _numberOfCopies,
        string[] memory _tokenIpfsHash
    ) public onlyRole(MINTER_ROLE) whenNotPaused {
        require(
            _tokenIpfsHash.length == _numberOfCopies,
            "Metadata URIs not equal to editions"
        );

        for (uint256 i = 0; i < _numberOfCopies; i++) {
            // mint the token
            _safeMint(to, _tokenIdCounter.current());

            // set token URI
            _setTokenURI(_tokenIdCounter.current(), _tokenIpfsHash[i]);

            // set token ID creator
            tokenIdToCreator[_tokenIdCounter.current()] = _creator;

            // emit event
            emit ArtistNFTMinted(
                _tokenIdCounter.current(),
                1,
                address(this),
                tokenURI(_tokenIdCounter.current()),
                _creator
            );

            // increment tokenId
            _tokenIdCounter.increment();
        }
    }

    function getCreator(uint256 _tokenId) public view returns (address) {
        return tokenIdToCreator[_tokenId];
    }

    /**
     * @dev overloaded function to return custom base uri
     */
    function _baseURI()
        internal
        view
        override(ERC721Upgradeable)
        returns (string memory)
    {
        return baseURI;
    }

    function setBaseURI(string memory _newBaseURI)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        baseURI = _newBaseURI;
    }

    // The following functions are overrides required by Solidity.

    function _burn(uint256 tokenId)
        internal
        override(ERC721Upgradeable, ERC721URIStorageUpgradeable)
    {
        super._burn(tokenId);
    }

    function royaltyInfo(uint256 _tokenId, uint256 _salePrice)
        external
        view
        returns (address receiver, uint256 royaltyAmount)
    {
        royaltyAmount = (artistRoyalty * _salePrice) / INVERSE_BASIS_POINTS;
        receiver = tokenIdToCreator[_tokenId];
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    )
        internal
        override(ERC721Upgradeable, ERC721EnumerableUpgradeable)
        whenNotPaused
    {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721Upgradeable, ERC721URIStorageUpgradeable)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    /**
     * @dev Puclic function(only admin authorized) to pause the contract
     */
    function pause() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Puclic function(only admin authorized) to unpause the contract
     */
    function unpause() public onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(
            ERC721Upgradeable,
            ERC721EnumerableUpgradeable,
            AccessControlUpgradeable
        )
        returns (bool)
    {
        return
            super.supportsInterface(interfaceId) ||
            type(IERC2981).interfaceId == interfaceId;
    }
}

