// SPDX-License-Identifier: GPL-3.0-only
pragma solidity ^0.8.0;

contract Reef_Governance{
    struct Projects{
        uint256 uniqueId; //need a way to generate uniqueid
        string projectName;
        string publisher;
        uint upVotes;
        uint downVotes;
        string uri;
    }
    
    //array of projects
    Projects[] public projects;
    
    //a counter to assign unique ids to projects
    uint256 counter = 1;
    
    //events for the contract
    
    //event to be emitted when a new project added
    event ReturnMessage(string successfullyAddedProject);
    
    
    
    //mapping of the contract
    
    //mapping to keep track of all the verified voters
    mapping(address => address) verifiedVoters;
    
    
    
    
    //to keep track of upvotes or downvotes casted on a project by mapping it to the voter address
    mapping(uint => address[]) uniqueIdToUpVoter;
    mapping(uint => address[]) uniqueIdToDownVoter;
    
    //to directly get proposal index through uniqueid
    mapping(uint => uint) uniqueIdToProposalIndex;
    
    
    
    //functions for the contract
    
    //function to increase the counter
    function incrementCounter() internal{
        counter = counter+1;
    }
    
    //add a project to the contract
    function addProject(string memory _projectName, string memory  _publisher, string memory _uri) public {
        Projects storage newProject = projects.push();
        newProject.upVotes = 0;
        newProject.downVotes = 0;
        newProject.uri = _uri;
        newProject.publisher = _publisher;
        newProject.projectName = _projectName;
        newProject.uniqueId = counter;
        uniqueIdToProposalIndex[counter]=projects.length-1;
        incrementCounter();
        emit ReturnMessage("Added the project successfully");
    }
    
    //verify a voter
    function verifyVoter(address _voter) public {
        //require not in verifiedVoters mapping
        //check for tokens in reef wallet
        //check for nft connected to the reef wallet
    }
    
    //upvote a project    
    function upVote(uint _uniqueId, address _voter) public {
        //require(uniqueIdToProposalIndex[_uniqueId]>,"This Project doesn't exist ");
        emit ReturnMessage("upvote started");
        uint i=uniqueIdToProposalIndex[_uniqueId];
        for(uint j=0;j<uniqueIdToDownVoter[_uniqueId].length;j++){
            if(uniqueIdToDownVoter[_uniqueId][j] ==_voter){
                projects[i].downVotes -=1;                        
                delete(uniqueIdToDownVoter[_uniqueId][j]);
            }                    
        }
        if(uniqueIdToUpVoter[_uniqueId].length == 0){
            projects[i].upVotes +=1;                     
            uniqueIdToUpVoter[_uniqueId].push(_voter);
        }
        else{
            uint256 temp = 0;
            for(uint j=0;j<uniqueIdToUpVoter[_uniqueId].length;j++){
                if(uniqueIdToUpVoter[_uniqueId][j] ==_voter){
                    temp=1;                                                 
                }                    
            }
            if(temp==0){
                projects[i].upVotes +=1;                     
                uniqueIdToUpVoter[_uniqueId].push(_voter); 
            }
        }    
        
        emit ReturnMessage("upvote successful");
    }
    
    //downvote a project
    function downVote(uint _uniqueId, address _voter) public {
        emit ReturnMessage("downvote started");
        uint i=uniqueIdToProposalIndex[_uniqueId];
        for(uint j=0;j<uniqueIdToUpVoter[_uniqueId].length;j++){
            if(uniqueIdToUpVoter[_uniqueId][j] ==_voter){
                projects[i].upVotes -=1;                        
                delete(uniqueIdToUpVoter[_uniqueId][j]);
            }                    
        }
        if(uniqueIdToDownVoter[_uniqueId].length == 0){
            projects[i].downVotes +=1;                     
            uniqueIdToDownVoter[_uniqueId].push(_voter);
        }
        else{
            uint temp=0;
            for(uint j=0;j<uniqueIdToDownVoter[_uniqueId].length;j++){
                if(uniqueIdToDownVoter[_uniqueId][j] ==_voter){
                    temp=1;              
                }                   
            }
            if(temp==0){
                projects[i].downVotes +=1;                     
                uniqueIdToDownVoter[_uniqueId].push(_voter);
            }
        }
        emit ReturnMessage("downvote completed");
    }
    
    //number of likes on a particular project
    function getUpVotes(uint _uniqueId) public view returns(uint256 _upVotes){  
        uint i=uniqueIdToProposalIndex[_uniqueId];    
        return projects[i].upVotes;
    }
    
    //number of dislikes on a particular project
    function getDownVotes(uint _uniqueId) public view returns(uint256 _downVotes){
        uint i=uniqueIdToProposalIndex[_uniqueId];
        return projects[i].upVotes;
    }
    
    //get the details of a contract given the  
    function projectDetail(uint _uniqueId) view public returns(Projects memory){
        return projects[uniqueIdToProposalIndex[_uniqueId]];
    }
    
    //number of votes in a proposal
    function noOfVotes(uint _uniqueId) view public returns(uint256){
        uint256 tempNumberUp;
        uint256 tempNumberDown;
        uint256 i= uniqueIdToProposalIndex[_uniqueId];
        tempNumberDown = projects[i].downVotes;
        tempNumberUp = projects[i].upVotes;        
        uint256 tempNumber = tempNumberUp - tempNumberDown;
        return tempNumber;
    }
    
    //display all proposals
    function viewAllProjects() view public returns(Projects[] memory){      
        return projects;
    }

    //if Proposer wants to remove his proposal
    function removeProjects(uint _uniqueId, address _voter) public {
        uint256 i=uniqueIdToProposalIndex[_uniqueId];
        if( keccak256(abi.encodePacked(projects[i].publisher))==keccak256(abi.encodePacked(_voter))){
            projects[i] = projects[projects.length - 1];
            projects.pop();
            delete uniqueIdToUpVoter[_uniqueId];
            delete uniqueIdToDownVoter[_uniqueId];
            delete uniqueIdToProposalIndex[_uniqueId];
            return;
        }
    }
    
    
    
    
}

