// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Burnable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Supply.sol";

contract PokemonToken is ERC1155, ERC1155Burnable, Ownable, ERC1155Supply {
    constructor() ERC1155("") {}

    function mint(
        address account,
        uint256 id,
        uint256 amount,
        bytes memory data)
        public
        onlyOwner
    {
        _mint(account, id, amount, data);
    }

    function mintBatch(
        address to,
        uint256[] memory ids, 
        uint256[] memory amounts,
        bytes memory data)
        public
        onlyOwner
    {
        _mintBatch(to, ids, amounts, data);
    }

    function _beforeTokenTransfer(
        address operator, 
        address from,
        address to, 
        uint256[] memory ids, 
        uint256[] memory amounts, 
        bytes memory data)
        internal
        override(ERC1155, ERC1155Supply)
    {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

contract PokemonGame{

    //structs of pokemon and trainer
    struct pokemon{
        string pokemonName;
        int pokemonId;
    }

    struct pokemonTrainer{
        address trainerAddress;
        pokemon [] trainerPokemons;
    }


    // maps address of trainer with all its pokemons
    mapping(address=>pokemonTrainer) public addressMappedTrainer;

    //trainers array
    address [] public trainers;

    //fetches pokemon by entering trainerAddress
    function getAllPokemonsOfTrainer(
        address _trainerAddress
        )
        public 
        view
        returns(pokemonTrainer memory)
        {
            return addressMappedTrainer[_trainerAddress];
        }

    //adds a new pokemon to pokemonTrainer
    function addNewPokemonForTrainer(
        address _trainerAddress,
        string memory _pokemonName,
        int _pokemonId)
        public
        {
            pokemon memory newPokemon = pokemon(_pokemonName,_pokemonId);
            addressMappedTrainer[_trainerAddress].trainerPokemons.push(newPokemon);
        }





}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

contract PokemonGame{

    //structs of pokemon and trainer
    struct pokemon{
        string pokemonName;
        int pokemonId;
    }

    struct pokemonTrainer{
        address trainerAddress;
        pokemon [] trainerPokemons;
    }


    // maps address of trainer with all its pokemons
    mapping(address=>pokemonTrainer) public addressMappedTrainer;

    //trainers array
    address [] public trainers;

    //fetches pokemon by entering trainerAddress
    function getAllPokemonsOfTrainer(
        address _trainerAddress
        )
        public 
        view
        returns(pokemonTrainer memory)
        {
            return addressMappedTrainer[_trainerAddress];
        }

    //adds a new pokemon to pokemonTrainer
    function addNewPokemonForTrainer(
        address _trainerAddress,
        string memory _pokemonName,
        int _pokemonId)
        public
        {
            bool checkIfTrainerExists = false;
            for(uint256 i=0;i<trainers.length;i++){
                if(trainers[i]==_trainerAddress){
                    checkIfTrainerExists=true;
                    break;
                }
            }

            if(checkIfTrainerExists==true){
            pokemon memory newPokemon = pokemon(_pokemonName,_pokemonId);
            addressMappedTrainer[_trainerAddress].trainerPokemons.push(newPokemon);
            }
            else{
                trainers.push(_trainerAddress);
                addNewPokemonForTrainer(_trainerAddress,_pokemonName,_pokemonId);
            }
        }





}

