// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/extensions/ERC1155Burnable.sol";

contract PokemonGame is ERC1155, Ownable, ERC1155Burnable {
    constructor() ERC1155("") {}

    function mint(
    address account,
    uint256 id,
    uint256 amount,
    bytes memory data)
        public
    {
        _mint(account, id, amount, data);
    }
    
    struct poke{
        uint256 tokenId;
        uint256 quantity;
    }
    
    function claimPokemon(
        address _claimer,
        uint256 _tokenId
    )public {
        require(_tokenId%2==0,"fuck you whore");
        mint(_claimer,_tokenId,1,bytes("bulbasaur"));
    }
    
    function evolvePokemon(
        address _claimer,
        uint256 _tokenId
        ) public {
        require(balanceOf(_claimer,_tokenId)>=2,"you don't have balls to evolve mf");
        burn(_claimer,_tokenId,2);
        mint(_claimer,_tokenId+2,1,bytes("bulbasaur"));
    }
    
    function yourPokemons(address _claimer,uint256 _tokenId)public view returns(uint256){
        return balanceOf(_claimer,_tokenId);
    }
    
}

