// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract RobertStake{
    uint256 public totalStaked;
    address owner;
    
    constructor(){
        totalStaked = 0;
        owner = msg.sender;
    }
    
    function stake(uint256 _amount) public {
        IERC20(0x1837fc93dC56E5A562D399dc76E1a25bc4a1AFAE).transfer(owner,_amount);
        totalStaked+=_amount;
    }
}

