pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract RobertStake{
    uint256 public totalStaked;
    address owner;
    
    constructor(){
        totalStaked = 0;
        owner = msg.sender;
    }
    
    function stake(uint256 _amount) public {
