//SPDX-License-Identifier:Unlicensed
pragma solidity ^0.8.17;

import "./ERC20Mod.sol";


contract CharToken is ERC20Mod{
    constructor(string memory tokenName,string memory tokenSymbol,uint8 _fee) ERC20Mod(tokenName,tokenSymbol,_fee){

    }
    string public desc;

    function setAmount(uint256 _amount)public{
         _mint(tx.origin,_amount);
    }

    function setDesc(string memory _desc) external{
        desc = _desc;
    }

    function getDesc() external returns(string memory){
        return desc;
    }
}

contract Deployer{

    address public deployedContractAddress;
    address [] public deployedContracts;
    uint256 public id;

    function getAllContracts() external returns(uint256){
        return id;
    }

    function deployNewContract(string memory tokenName,string memory tokenSymbol,uint8 fee,uint256 _amount,string memory desc) public {
        CharToken newToken = new CharToken(tokenName,tokenSymbol,fee);
        deployedContractAddress = address(newToken);
        newToken.setAmount(_amount);
        newToken.setDesc(desc);
        id++;
        deployedContracts.push(deployedContractAddress);
    }
}

