// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "hardhat/console.sol";

library Array {
   function insert(uint32[] storage arr, uint32 value) internal {
      arr.push(value);
   }

   //ToDo: fix it
   function orderedInsert(uint32[] storage arr, uint32 value) internal {
      arr.push(0);
      bool broken = false;
      for (uint32 i = uint32(arr.length - 1); i > 0; i--) {
         if (arr[i] > value) {
            arr[i] = arr[i - 1];
         } else {
            arr[i] = value;
            broken = true;
            break;
         }
      }
      if (!broken) {
         arr[0] = value;
      }
   }

   function remove(uint32[] storage arr, uint32 index) internal {
      require(arr.length > 0, "Can't remove from empty array");
      arr[index] = arr[arr.length - 1];
      arr.pop();
   }

   function orderedRemove(uint32[] storage arr, uint32 index) internal {
      require(arr.length > 0, "Can't remove from empty array");
      for (uint32 i = index; i < arr.length - 1; i++) {
         arr[i] = arr[i + 1];
      }
      arr.pop();
   }

   function findIndex(uint32[] storage arr, uint32 value) internal view returns (uint32) {
      for (uint32 i = 0; i < arr.length; i++) {
         if (arr[i] == value) {
            return i;
         }
      }

      revert("Couldn't find index");
   }
}

