// SPDX-License-Identifier: MIT
pragma solidity ^0.8.10;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

import "./AddressItems.sol";
import "hardhat/console.sol";

contract MulticollectionContract is Ownable, ERC721, AccessControl, AddressItems {
   using Strings for uint256;
   using ECDSA for bytes32;

   constructor(
      address superOwner_,
      string memory name_,
      string memory symbol_
   ) ERC721(name_, symbol_) {
      _superOwner = superOwner_;
      _grantRole(SUPER_OWNER_ROLE, _superOwner);
   }

   // variables, structs, modifiers, events ------------------------

   bytes32 private constant SUPER_OWNER_ROLE = keccak256("SUPER_OWNER_ROLE");
   bytes32 private constant OWNER_ROLE = keccak256("OWNER_ROLE");

   address private _superOwner;

   uint32 private _superOwnerPermissionTimeLimit;

   uint256 private _tokenIdCounter;
   uint256 private _sessionItemsCounter;

   mapping(bytes32 => SessionItem) private _sessionItems;

   bool private _isCommonTokenUri;

   string private _uri;
   string private _uriPostfix;

   struct SessionItem {
      address owner;
      uint32 tokenCount;
   }

   modifier onlySuperOwnerOrOwner() {
      require(
         hasRole(SUPER_OWNER_ROLE, _msgSender()) || hasRole(OWNER_ROLE, _msgSender()),
         "Only superOwner or owner has right to call this function"
      );
      _;
   }

   modifier onlySuperOwnerOrPermittedOwner() {
      require(
         isSuperOwnerOrPermittedOwner(_msgSender()),
         "Only superOwner or owner who has permission can call this function"
      );
      _;
   }

   event Claim(
      address indexed owner,
      uint32 indexed count,
      bytes32 indexed sessionIdHash,
      uint32 timestamp
   );

   event Mint(address indexed to, uint32 indexed assetNumber, uint32 timestamp);

   // functions ----------------------------------------------------

   function giveSuperOwnerPermissionToOwner(
      uint32 hourLimit
   ) external onlySuperOwnerOrPermittedOwner {
      _superOwnerPermissionTimeLimit = uint32(block.timestamp) + hourLimit * 3600;
   }

   function grantOwnerRoleToCurrentOwner() external onlySuperOwnerOrOwner {
      _grantRole(OWNER_ROLE, owner());
   }

   function superOwnerPermissionTimeLimit() external view returns (uint32) {
      return _superOwnerPermissionTimeLimit;
   }

   function isSuperOwnerOrPermittedOwner(address account) internal view returns (bool) {
      return
         hasRole(SUPER_OWNER_ROLE, account) ||
         (hasRole(OWNER_ROLE, account) && block.timestamp < _superOwnerPermissionTimeLimit);
   }

   function hasOwnerSuperOwnerPermission() public view returns (bool) {
      return isSuperOwnerOrPermittedOwner(owner());
   }

   function superOwner() public view returns (address) {
      return _superOwner;
   }

   function _mint(address to) private returns (uint32) {
      uint32 tokenId = uint32(_tokenIdCounter);
      _tokenIdCounter += 1;
      addTokenToAddressItems(to, tokenId);
      _safeMint(to, tokenId);
      return tokenId;
   }

   function mint(address to) public onlySuperOwnerOrPermittedOwner {
      uint32 assetNumber = _mint(to);
      emit Mint(to, assetNumber, uint32(block.timestamp));
   }

   function mint(address to, uint32 count) public onlySuperOwnerOrPermittedOwner {
      for (uint32 i = 0; i < count; i++) {
         mint(to);
      }
   }

   function burn(uint32 tokenId) public onlySuperOwnerOrPermittedOwner {
      address tokenOwner = ownerOf(tokenId);
      removeTokenFromAddressItems(tokenOwner, tokenId);
      _burn(tokenId);
   }

   function mintBatch(address[] calldata addresses) public onlySuperOwnerOrPermittedOwner {
      for (uint32 i = 0; i < addresses.length; i++) {
         mint(addresses[i]);
      }
   }

   function transferFrom(address from, address to, uint256 tokenId) public virtual override {
      transferTokenInAddressItems(from, to, uint32(tokenId));
      super.transferFrom(from, to, tokenId);
   }

   function safeTransferFrom(
      address from,
      address to,
      uint256 tokenId,
      bytes memory data
   ) public virtual override {
      transferTokenInAddressItems(from, to, uint32(tokenId));
      super.safeTransferFrom(from, to, tokenId, data);
   }

   function getTokenCount() public view returns (uint256) {
      return _tokenIdCounter;
   }

   function setCommonTokenUri(bool isCommonTokenUri) external onlySuperOwnerOrPermittedOwner {
      _isCommonTokenUri = isCommonTokenUri;
   }

   function getCommonTokenUri() external view returns (bool) {
      return _isCommonTokenUri;
   }

   function getURI() external view returns (string memory) {
      return _uri;
   }

   function setURI(string memory uri) external onlySuperOwnerOrPermittedOwner {
      _uri = uri;
   }

   function setURIPostfix(string memory uriPostfix) external onlySuperOwnerOrPermittedOwner {
      _uriPostfix = uriPostfix;
   }

   function tokenURI(uint256 tokenId) public view override returns (string memory) {
      string memory str1 = "";
      if (_isCommonTokenUri) {
         str1 = concatenateStrings(_uri, "token");
      } else {
         str1 = concatenateStrings(_uri, tokenId.toString());
      }
      string memory str2 = concatenateStrings(str1, _uriPostfix);
      return str2;
   }

   function contractURI() public view returns (string memory) {
      string memory str1 = concatenateStrings(_uri, "contract");
      return concatenateStrings(str1, _uriPostfix);
   }

   function _addSessionItem(address tokenOwner, uint32 tokenCount, bytes32 sessionHash) private {
      require(_sessionItems[sessionHash].owner == address(0), "This sessionId was used before");
      _sessionItemsCounter += 1;
      _sessionItems[sessionHash] = SessionItem(tokenOwner, tokenCount);
   }

   function _verifySignature(
      address tokenOwner,
      uint32 tokenCount,
      string memory sessionId,
      bytes memory signature
   ) private view returns (bool) {
      bytes32 messageHash = keccak256(abi.encodePacked(tokenOwner, tokenCount, sessionId));
      address recover = messageHash.toEthSignedMessageHash().recover(signature);
      return recover == superOwner() || recover == owner();
   }

   function claimSig(
      address tokenOwner,
      uint32 tokenCount,
      string memory sessionId,
      bytes memory signature
   ) external {
      require(_verifySignature(tokenOwner, tokenCount, sessionId, signature), "Invalid signature");
      bytes32 sessionIdHash = keccak256(abi.encodePacked(sessionId));
      _addSessionItem(tokenOwner, tokenCount, sessionIdHash);
      for (uint32 i = 0; i < tokenCount; i++) {
         _mint(tokenOwner);
      }
      emit Claim(tokenOwner, tokenCount, sessionIdHash, uint32(block.timestamp));
   }

   // The following functions are overrides required by Solidity.
   function supportsInterface(
      bytes4 interfaceId
   ) public view override(ERC721, AccessControl) returns (bool) {
      return super.supportsInterface(interfaceId);
   }

   function concatenateStrings(
      string memory a,
      string memory b
   ) internal pure returns (string memory) {
      // Convert input strings to byte arrays
      bytes memory byteArrayA = bytes(a);
      bytes memory byteArrayB = bytes(b);

      // Calculate total length of the concatenated string
      uint totalLength = byteArrayA.length + byteArrayB.length;

      // Create a new dynamic byte array of the total length
      bytes memory result = new bytes(totalLength);
      // Loop through the first input string and copy its bytes to the result array
      uint i;
      uint j;
      for (i = 0; i < byteArrayA.length; i++) {
         result[j++] = byteArrayA[i];
      }
      // Loop through the second input string and copy its bytes to the result array
      for (i = 0; i < byteArrayB.length; i++) {
         result[j++] = byteArrayB[i];
      }
      // Convert the result byte array back to a string and return it
      return string(result);
   }
}

